// BENCHES

macro_rules! bench {
    ($fn:ident, $name:literal, $iter:expr) => {
        fn $fn(criterion: &mut Criterion) {
            let mut group = criterion.benchmark_group($name);
            group.measurement_time(Duration::from_secs(5));
            parse_float_generator!(group, "f64", $iter, f64);
        }
    };
}

bench!(fast, "contrived:fast", [FAST_PATH].iter());
bench!(disguised, "contrived:disguised", [DISGUISED_FAST_PATH].iter());
